mapper.select:
select d5.BIANMA BIANMA5, d5.NAME DNAME5, d6.BIANMA BIANMA6, d6.NAME DNAME6,
from f left join d5 on f.SUBJECT = d5.BIANMA left join d6 on f.CLASSHOUR = d6.BIANMA where 1=1
and ( f.TITE LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or f.LECTURER LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or f.REMARKS LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') )
and ( (f.AUTHORIZED = '' or f.AUTHORIZED is null) or locate(#{pd.ROLE_ID},f.AUTHORIZED) ) and ( (f.PERSONNELID = '' or f.PERSONNELID is null) or locate(#{pd.USERNAME},f.PERSONNELID) )
and d5.BIANMA = #{pd.SUBJECT}
and d6.BIANMA = #{pd.CLASSHOUR}
